<script setup>
import card from '~/components/rus/cardComponent.vue'
import { useProductsStore } from '~/store/rus/products'
import { useCounterStore } from '~/store/rus/counter'
const productsStore = useProductsStore()
const counterStore = useCounterStore()

productsStore.getProducts()

const removeAllFavorites = () => {
    productsStore.data.products = []
    localStorage.setItem('favoritesRus', JSON.stringify([]))
    counterStore.deleteMany('favorites')
}

const removeFavorites = (id) => {
    productsStore.data.products = productsStore.data.products.filter(product => product._id !== id);
    localStorage.setItem('favoritesRus', JSON.stringify(productsStore.data.products));

    productsStore.updateProductInFavorites(id, false);

    counterStore.deleteOne('favorites');
};
function click(e) {
    if (e.target.id == 'removeBtn') {
        removeFavorites(e.target.closest('.card').id);
    }
}
</script>